userDefinedLinkHintCss:
:root {
  --vimium-general-font-size: 18;
  --vimium-general-font-weight: 500;
  --vimium-li-font-size: 14;
  --vimium-li-font-weight: 300;
  --vimium-font: Noto Sans CJK JP;
  --vimium-padding: 2px;
  --vimium-shadow: 0 2px 4px rgba(0, 0, 0, 0.12), 0 1px 2px rgba(0, 0, 0, 0.24);

  /* ---------- NORD ---------- */
  --vimium-fg: #E5E9F0;
  --vimium-bg: #2E3440;
  --vimium-border: #3B4252;
  --vimium-main-fg: #88C0D0;
  --vimium-accent-fg: #A3BE8C;

  /* Unused Alternate Colors */
  /* --bg-dark: #4C566A; */
  /* --main-fg-alt: #5E81AC; */
  /* --orange: #D08770; */
  /* --red: #BF616A; */
  /* --yellow: #EBCB8B; */
}

/* -------- HINTS -------- */
#vimiumHintMarkerContainer div.internalVimiumHintMarker, #vimiumHintMarkerContainer div.vimiumHintMarker {
  background: var(--vimium-bg);
  border: 1px solid var(--vimium-border);
  box-shadow: var(--vimium-shadow);
  padding: 3px 4px;
}i

#vimiumHintMarkerContainer div span {
  color: var(--vimium-main-fg);
  font-family: var(--vimium-font);
  font-size: var(--vimium-general-font-size);
  font-weight: var(--vimium-general-font-weight);
  text-shadow: none;
}

#vimiumHintMarkerContainer div > .matchingCharacter {
  opacity: 0.3;
}

#vimiumHintMarkerContainer div > .matchingCharacter ~ span {
  color: var(--vimium-main-fg);
}

/* -------- VOMNIBAR -------- */
#vomnibar {
  animation: show 200ms cubic-bezier(0, 0, 0.2, 1) forwards;
  background: var(--vimium-bg);
  border: none;
  box-shadow: var(--vimium-shadow);
}

/* Animate Slide in */
@keyframes show {
  0% {
    opacity: 0;
    transform: translateY(50px);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
}

#vomnibar .vomnibarSearchArea,
#vomnibar input {
  background: transparent;
  border: none;
  box-shadow: none;
  color: var(--vimium-fg);
  font-family: var(--vimium-font);
  font-size: var(--vimium-general-font-size);
  font-weight: var(--vimium-general-font-weight);
}

#vomnibar .vomnibarSearchArea {
  padding: var(--vimium-padding) 30px;
}

#vomnibar input {
  padding: var(--vimium-padding);
}

#vomnibar ul {
  background: var(--vimium-bg);
  border-top: 1px solid var(--vimium-border);
  margin: 0;
  padding: var(--vimium-padding);
}

#vomnibar li {
  border-bottom: 1px solid var(--vimium-border);
  padding: 2px 2px 6px 14px;
}

#vomnibar li .vomnibarTopHalf {
  padding-top: var(--vimium-padding);
}

#vomnibar li .vomnibarBottomHalf {
  padding-bottom: var(--vimium-padding);
}

#vomnibar li .vomnibarSource {
  color: var(--vimium-main-fg);
  font-family: var(--vimium-font);
  font-size: var(--vimium-li-font-size);
  font-weight: var(--vimium-li-font-weight);
}

#vomnibar li em,
#vomnibar li .vomnibarTitle {
  color: var(--vimium-main-fg);
  font-family: var(--vimium-font);
  font-size: var(--vimium-li-font-size);
  font-weight: var(--vimium-li-font-weight);
}

#vomnibar li .vomnibarUrl {
  color: var(--vimium-fg);
  font-family: var(--vimium-font);
  font-size: var(--vimium-li-font-size);
  font-weight: var(--vimium-li-font-weight);
}

#vomnibar li .vomnibarMatch {
  color: var(--vimium-accent-fg);
  font-weight: normal;
}

#vomnibar li .vomnibarTitle .vomnibarMatch {
  color: var(--vimium-main-fg);
}

#vomnibar li.vomnibarSelected {
  background-color: var(--vimium-border);
}

/* -------- HUD -------- */
div.vimiumHUD {
  background: var(--vimium-bg);
  border: 1px solid var(--vimium-border);
  box-shadow: var(--vimium-shadow);
}

div.vimiumHUD span#hud-find-input,
div.vimiumHUD .vimiumHUDSearchAreaInner {
  color: var(--vimium-fg);
  font-family: var(--vimium-font);
  font-size: var(--vimium-general-font-size);
  font-weight: var(--vimium-general-font-weight);
}

div.vimiumHUD .hud-find {
  background-color: transparent;
  border: none;
}

div.vimiumHUD .vimiumHUDSearchArea {
  background-color: transparent;
}
